import React, { Component } from 'react';
//import Register from './Registration';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import API from "../utils/API";
//import Test from '../components/Nav';
//import RegButton from '../components/Buttons';
//import Owl from '../components/Carousel';
import { Col, Row, Container } from "../components/Grid"; 
//import Jumbotron from "../components/Jumbotron";
//import Image from "../components/Image";
import friend from '../app.json';
//import Stash from "../components/StashCon";
//import API from "../utils/API";
//import logo from "../images/buildmasterlogo.png";
//import POE from "../images/POE.png";
import './pages.css';
import {Redirect} from 'react-router-dom';
//import SignIn from '../components/SignIn';
//import {ButtonToolbar} from 'react-bootstrap';
//import { DropdownButton, MenuItem, ButtonToolbar} from 'react-bootstrap';
//import ThemeSwitcher from '../components/SignIn';//will need to change name for dropdown
//import Test from '../components/StashCon';//modal will need to change
//import test from '../images/test.png';
//import modal from '../components/StashCon';
import {Nav, SignReg} from "../components/Nav";
//import SignReg from "../components/Nav";
//import background from "../images/background.png";










class Home extends Component {


  state = {
    friend,
    users: [],
    search:"",
    ascension: "",
    style: "",
    redirect: false,
    username:"",
    password:""
   
   
    
  };

  componentDidMount() {
    this.loadHome();
    
  }

  loadHome = () => {
    API.getUsers()
      .then(res =>
        this.setState({ users: res.data, username:"", password:""})
      )
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.username && this.state.password) {
      API.saveUser({
        username: this.state.username,
        password: this.state.password
       
      })
        .then(res => this.loadHome())
        .catch(err => console.log(err));
    }
  };


  setRedirect = () => {
   this.setState({
     redirect:true
    })
  };

  regisRedirect = () => {
    
    if (this.state.redirect) {
      return<Redirect to='/registration'/>
    }
  }
  
    test = () => {
      console.log("just testing")
    }
 
    render() {
      return (
        <Container>
          <Row>
            <Col size="md">
              <div>
              <SignReg/>
              
               
               
               
             
                
              </div>           
            </Col>  
                    
          </Row>
          <Row>
          <Nav/>
          </Row>
         
          <Row>
          
          <Col size="md-12">
              
              
               
               <div>
               <Owl/>
                
               </div>
               
                  
                     
             
            </Col>            
          </Row>
         
          <Row>
            <Col size="md">
              <div>
              <p>
              It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English. Many desktop publishing packages and web page editors now use Lorem Ipsum as their default model text, and a search for 'lorem ipsum' will uncover many web sites still in their infancy.</p>
              </div>
            </Col>
          </Row>
        </Container>
      );
    }
  }

  
  
  export default Home;
