import React from "react";
import { Col, Row, Container } from "../Grid";
//import Nav from '../Nav';
import "../Forms/index.css";
import { DocumentProvider } from "mongoose";

// This file exports the Input, TextArea, and FormBtn components

export function Input(props) {
  return (
    <div classNameName="form-group">
      <input classNameName="form-control" {...props} />
    </div>
  );
}

export function TextArea(props) {
  return (
    <div classNameName="form-group">
      <textarea classNameName="form-control" rows="20" {...props} />
    </div>
  );
}

export function FormBtn(props) {
  return (
    <button {...props} style={{ float: "right", marginBottom: 10 }} classNameName="btn btn-success">
      {props.children}
    </button>
  );
}

 export function SigninForm() {
    return (
      <div className="container">
      <div className="container">
        <form action="#">
          <div className="row">
            <h2 style={{textAlign:"center"}}>Login with Social Media or Manually</h2>
            <div className="vl">
              <span className="vl-innertext">or</span>
            </div>
            <div className="col">
              <a href="#" className="fb btn">
                <i className="fa fa-facebook fa-fw"></i> Login with Facebook
              </a>
              <a href="#" className="twitter btn">
                <i className="fa fa-twitter fa-fw"></i> Login with Twitter
              </a>
              <a href="#" className="google btn">
                <i className="fa fa-google fa-fw"></i> Login with Google+
              </a>
            </div>
            <div className="col">
              <div className="hide-md-lg">
                <p>Or sign in manually:</p>
              </div>

              <input type="text" name="username" placeholder="Username" required/>
              <input type="password" name="password" placeholder="Password" required/>
              <input type="submit" value="Login"/>
            </div>
          </div>
        </form>
      </div>
      <div className="bottom-container">
        <div className="row">
          <div className="col">
            <a href="" style={{color:"white"}} className="btn">Sign up</a>
          </div>
          <div className="col">
            <a href="#" style={{color:"white"}} className="btn">Forgot password?</a>
          </div>
        </div>
      </div>
      </div>
    )
}

export default SigninForm;
